    function msg_tips(options) {
        var defaults = {
            timeout: 3000,
            //
            msg: "消息提示框",
            //
            speed: 300,
            //
            type: "success"//1、success 2、error 3、warning
        };
        g_web = mx("#web");
        dom_create_child(g_web, "div", "tip_div");
        var width = mx("#page").offsetWidth;
        var bid = parseInt(Math.random() * 100000);
        jQuery("#tip_div").html('<div id="tip_container' + bid + '" class="container tip_container"><div id="tip' + bid + '" class="mtip"><i></i><span id="tsc' + bid + '"></span><i id="mclose' + bid + '" class="mclose"></i></div></div>');
        var $this = jQuery(this);
        var $tip_container = jQuery("#tip_container" + bid)
        var $tip = jQuery("#tip" + bid);
        var $tipSpan = jQuery("#tsc" + bid);
        var $colse = jQuery("#mclose" + bid);
        //
        $tip_container.css({'margin-left': '0', left: '0px', width: width});
        $tipSpan.css({width: width - 60, 'text-align': 'center'});
        clearTimeout(window.timer);

        $tip.attr("class", options.type).addClass("mtip");
        $tipSpan.html(options.msg);
        $tip_container.slideDown(300);
        //
        window.timer = setTimeout(function () {
            $tip_container.slideUp(300);
        }, options.timeout);
        //
        $tip_container.live("mouseover", function () {
            clearTimeout(window.timer);
        });
        //
        $tip_container.live("mouseout", function () {
            window.timer = setTimeout(function () {
                $tip_container.slideUp(300);
            }, options.timeout);
        });
        //
        $colse.live("click", function () {
            $tip_container.slideUp(300);
        });
    }


