    function call_native(func, param, callback)
    {
        if (g_ios_browser == 1) {
                   call_oc(func, param, callback);
               } else if(g_android_browser == 1){
                 call_android(func, param, callback);
               }
    }

    function call_android(func, param, callback){
        if(func == "get_native_param" && param != "&loadweb=class_user_feedback")
        {//alert(func);
            window.McldActivityWebHybrid.getUrlJson(param);
        }
        else if(func == "display_QR_code")
        {//alert(func);
            window.McldActivityWebHybrid.startActivityObject(func,param,callback);
        }
        else if(func == "creat_new_page")
        {//alert(func);
            window.McldActivityWebHybrid.startActivityObject(func,param,callback);
        }
        else if(func == "add_device_forget_password_close")
        {
            window.McldActivityWebHybrid.finishActivity();
        }
        else if(func == "get_device_sn_existed--")
        {//alert(func);
            window.McldActivityWebHybrid.getUrlJson(param);
        }
        else if(func == "add_device_wifi_name")
        {
            window.McldActivityWebHybrid.getWifiName(param);
        }
        else if(func == "return_devices_list")
        {
            window.McldActivityWebHybrid.returnDevicesList();
        }
        else if(func == "backAddDevicePage")
        {alert(func);
        //&title=mcs_add_device&htmlName=add_device_html/add_device_input_device_password.html&mode=push&leftButton=back&rightButton=close
            window.McldActivityWebHybrid.startActivityObject(func,"&title=mcs_add_device&htmlName=add_device_html/add_device_input_device_password.html&mode=push&leftButton=back&rightButton=close","");
        }
        else if(func == "set_pass")
        {
            window.McldActivityWebHybrid.setPass(param);
        }
        else if (func == "get_native_param" && param == "&loadweb=class_user_feedback")
        {//LKK add
            //alert("call native");
            window.McldActivityUserFeedback.getUrlJson(param);
        }else if(func == "feedback_img_get")
        {
             //alert("Call android for photo");
             window.McldActivityUserFeedback.accessPhoto();
        }
				
    }

    function call_oc(func, param, callback)
    {
            var iframe = document.createElement("iframe");
            var url = "iosapp:" + "&func=" + func + param + "&callback=" + callback;
            iframe.src = url;
            iframe.style.display = 'none';
            document.body.appendChild(iframe);
            iframe.parentNode.removeChild(iframe);
            iframe = null;
    }

